mapper.select:
SELECT

FROM t_config a
where a.id=#{id}

SELECT

FROM t_config a

    a.del_flag=#{NORMAL_FLAG}

        and a.label=#{label}


        and a.type=#{type}


        and a.value=#{value}


order by a.order_num

mapper.update:
update t_config a
set a.del_flag=#{DEL_FLAG}
where id=#{id}
