mapper.select:
SELECT

FROM sys_dict a
where a.id=#{id}

SELECT

FROM sys_dict a

    a.del_flag=#{NORMAL_FLAG}

        and a.code=#{code}


        and a.type=#{type}


        and a.parent_id=#{parentId}


order by a.order_num

mapper.update:
update sys_dict a
set a.del_flag=#{DEL_FLAG}
where id=#{id}
