mapper.select:
select

from t_menu a
where id = #{id}

select

from t_menu a

    a.del_flag=#{NORMAL_FLAG}

        and a.name=#{name}


        and a.parent_id=#{parentId}

select
from t_menu a
a.del_flag='0' and b.del_flag='0' and b.role_id = #{roleId}

mapper.insert:
insert into t_menu (
    id, name, url, perms, type, status, order_num, icon, parent_id,
    create_by, create_date, update_by, update_date, del_flag
)
values (
    #{id}, #{name}, #{url}, #{perms}, #{type}, #{status}, #{orderNum}, #{icon}, #{parentId},
    #{createBy}, #{createDate},#{updateBy}, #{updateDate}, #{delFlag}
  )

mapper.update:
update t_menu a
set a.del_flag=#{DEL_FLAG}
where id=#{id}
