mapper.select:
select

from t_menu a
where id = #{id}

select

from t_menu a

    a.del_flag=#{NORMAL_FLAG}

        and a.name=#{name}


        and a.parent_id=#{parentId}

select
from t_menu a inner join t_role_menu b on a.id=b.menu_id
a.del_flag='0' and b.role_id = #{roleId}

select
from t_menu a inner join t_role_menu b on a.id=b.menu_id inner join t_user_role c on c.role_id=b.role_id
a.del_flag='0' and exists ( select 1 from t_role r where r.del_flag='0' and r.id=b.role_id and r.status = '1' ) and c.user_id = #{userId}

mapper.insert:
insert into t_menu (
    id, name, url, perms, type, status, open, order_num, icon, parent_id,
    create_by, create_date, update_by, update_date, del_flag
)
values (
    #{id}, #{name}, #{url}, #{perms}, #{type}, #{status}, #{open}, #{orderNum}, #{icon}, #{parentId},
    #{createBy}, #{createDate},#{updateBy}, #{updateDate}, #{delFlag}
  )

mapper.update:
update t_menu a
set a.del_flag=#{DEL_FLAG}
where id=#{id}
