mapper.select:
select

from t_role a
where id = #{id}

select

from t_role a

    a.del_flag=#{NORMAL_FLAG}

        and a.name=#{name}

select
from t_role a
a.del_flag='0' and b.user_id = #{userId}

mapper.insert:
insert into t_role (id, name, code,
    status, create_by, create_date,
    update_by, update_date, del_flag
  )
values (#{id}, #{name}, #{code},
    #{status}, #{createBy}, #{createDate},
    #{updateBy}, #{updateDate}, #{delFlag}
  )

mapper.update:
update t_role a
set a.del_flag=#{DEL_FLAG}
where id=#{id}
